import React, { useState } from 'react';
import styles from './ManageServices.module.css';
import axios from 'axios';

const ManageServices = () => {
  const [services, setServices] = useState(['Plumbing', 'Electricity']);
  const [newService, setNewService] = useState('');

  const addService = () => {
  if (newService) {
    const serviceData = {
      name: newService
    };

    axios.post("http://localhost:8080/request/data", serviceData)
      .then((response) => {
        console.log("Service added:", response.data);
        setServices([...services, newService]);
        setNewService('');
      })
      .catch((error) => {
        console.error("Error adding service:", error);
      });
  }
};
  const removeService = (serviceToRemove) => {
    setServices(services.filter(service => service !== serviceToRemove));
  };

  return (
    <div className={styles.services}>
      <h3>Manage Services</h3>
      <input type="text" placeholder="New Service" value={newService} onChange={(e) => setNewService(e.target.value)}/>
      <button onClick={addService}>Add Service</button>
      <ul>
        {services.map((service, index) => (
          <li key={index}>
            {service}
            <button onClick={() => removeService(service)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageServices;
