// components/User/RaiseRequest.jsx
import React, { useState } from 'react';
import styles from './RaiseRequest.module.css';

const RaiseRequest = () => {
  const [request, setRequest] = useState({
    type: '',
    description: '',
    preferredTime: ''
  });

  const handleChange = (e) => {
    setRequest({ ...request, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Service Request:', request);
    alert("Request submitted successfully!");
    setRequest({ type: '', description: '', preferredTime: '' });
  };

  return (
    <form className={styles.form} onSubmit={handleSubmit}>
      <h2>Raise a Service Request</h2>
      <select name="type" value={request.type} onChange={handleChange} required>
        <option value="">Select Service</option>
        <option value="Electricity">Electricity</option>
        <option value="Water">Water</option>
        <option value="Internet">Internet</option>
        <option value="Cleaning">Cleaning</option>
      </select>
      <textarea name="description" placeholder="Describe your issue" value={request.description} onChange={handleChange} required />
      <input type="datetime-local" name="preferredTime" value={request.preferredTime} onChange={handleChange} required />
      <button type="submit">Submit Request</button>
    </form>
  );
};

export default RaiseRequest;
