import React, { useState } from 'react'
import SubjectRow from '/.SubjectRow'
const SemesterForm = (semNo,onComplete) => {
    const [subjects,setSubjects]= useState([]);
    const addSubject =()=>{
        setSubjects([...sub, {name:'',grade:'',credit:''}]);

    }
    const updateSubject = (index,field,value)=>{
        const updated = [...sub];
        updated[index][field]=value;
        setSub(updated);
    };
  return (
    <>
    <h3>Semester {semNo}</h3>
    {sub.map((subj,i)=>(
        <SubjectRow key={i} subject={subj} onChange ={(field,value)=>updateSubject(i,field,value)}/>
    ))}
    <button onClick={addSubject}>Add Subject</button>
    <button onClick={()=>onComplete({semNo,sub})}>Save Semester</button>

    </>
  )
}

export default SemesterForm
