import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './RaiseRequest.module.css';

const RaiseRequest = () => {
  const [services, setServices] = useState([]);
  const [requests, setRequests] = useState([]);
  const [selectedService, setSelectedService] = useState('');
  const [description, setDescription] = useState('');
  const [message, setMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [loading, setLoading] = useState(true);
  const [editingRequest, setEditingRequest] = useState(null);
  const navigate = useNavigate();
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    // Fetch services and user's requests
    Promise.all([
      axios.get('http://localhost:8080/services/data'),
      axios.get(`http://localhost:8080/requests/user/${userId}`)
    ])
    .then(([servicesRes, requestsRes]) => {
      setServices(servicesRes.data);
      setRequests(requestsRes.data);
      setLoading(false);
    })
    .catch(error => {
      console.error('Error fetching data:', error);
      setMessage('Failed to load data. Please try again later.');
      setIsError(true);
      setLoading(false);
    });
  }, [userId]);

  const formatDateForMySQL = (date) => {
    const pad = (n) => (n < 10 ? '0' + n : n);
    return (
      date.getFullYear() + '-' +
      pad(date.getMonth() + 1) + '-' +
      pad(date.getDate()) + ' ' +
      pad(date.getHours()) + ':' +
      pad(date.getMinutes()) + ':' +
      pad(date.getSeconds())
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!selectedService || !description.trim()) {
      setMessage('Please fill all fields');
      setIsError(true);
      setTimeout(() => setMessage(''), 3000);
      return;
    }

    setIsSubmitting(true);

    const now = new Date();
    const tomorrow = new Date(Date.now() + 86400000);

    const requestData = {
      serviceId: parseInt(selectedService),
      userId: parseInt(userId),
      description: description.trim(),
      status: "Pending",
      createDate: formatDateForMySQL(now),
      exceptCompletionDate: formatDateForMySQL(tomorrow)
    };

    const apiCall = editingRequest 
      ? axios.put(`http://localhost:8080/requests/${editingRequest.requestId}`, requestData)
      : axios.post('http://localhost:8080/requests/save', requestData);

    apiCall
      .then(response => {
        setSelectedService('');
        setDescription('');
        setEditingRequest(null);
        setIsError(false);
        setMessage(`Request ${editingRequest ? 'updated' : 'submitted'} successfully!`);
        
        // Refresh requests list
        return axios.get(`http://localhost:8080/requests/user/${userId}`);
      })
      .then(response => {
        setRequests(response.data);
        setTimeout(() => navigate('/userdashboard'), 2000);
      })
      .catch(error => {
        console.error("Error:", error);
        setMessage(error.response?.data?.message || 'Something went wrong. Try again.');
        setIsError(true);
      })
      .finally(() => {
        setIsSubmitting(false);
        setTimeout(() => setMessage(''), 5000);
      });
  };

  const handleEdit = (request) => {
    setEditingRequest(request);
    setSelectedService(request.serviceId.toString());
    setDescription(request.description);
    window.scrollTo(0, 0); // Scroll to form
  };

  const handleDelete = (requestId) => {
    if (window.confirm('Are you sure you want to delete this request?')) {
      axios.delete(`http://localhost:8080/requests/${requestId}`)
        .then(() => {
          setMessage('Request deleted successfully!');
          setRequests(requests.filter(req => req.requestId !== requestId));
          setTimeout(() => setMessage(''), 3000);
        })
        .catch(error => {
          console.error("Delete error:", error);
          setMessage('Failed to delete request');
          setIsError(true);
          setTimeout(() => setMessage(''), 3000);
        });
    }
  };

  const handleCancelEdit = () => {
    setEditingRequest(null);
    setSelectedService('');
    setDescription('');
  };

  if (loading) return <div className={styles.loading}>Loading...</div>;

  return (
    <div className={styles.container}>
      <h2>{editingRequest ? 'Edit Request' : 'Raise New Request'}</h2>

      {message && (
        <div className={isError ? styles.error : styles.success}>
          {message}
        </div>
      )}

      <form onSubmit={handleSubmit} className={styles.form}>
        <div className={styles.formGroup}>
          <label htmlFor="service">Select Service:</label>
          <select
            id="service"
            value={selectedService}
            onChange={(e) => setSelectedService(e.target.value)}
            className={styles.select}
            required
            disabled={isSubmitting || services.length === 0}
          >
            <option value="">-- Select a service --</option>
            {services.map(service => (
              <option key={service.serviceId} value={service.serviceId}>
                {service.serviceName}
              </option>
            ))}
          </select>
        </div>

        <div className={styles.formGroup}>
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            placeholder="Describe your request in detail..."
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className={styles.textarea}
            required
            disabled={isSubmitting}
          />
        </div>

        <div className={styles.buttonGroup}>
          <button
            type="submit"
            className={styles.submitButton}
            disabled={isSubmitting || !selectedService}
          >
            {isSubmitting 
              ? 'Submitting...' 
              : editingRequest ? 'Update Request' : 'Submit Request'}
          </button>
          {editingRequest && (
            <button
              type="button"
              onClick={handleCancelEdit}
              className={styles.cancelButton}
              disabled={isSubmitting}
            >
              Cancel Edit
            </button>
          )}
          <button
            type="button"
            onClick={() => navigate('/userdashboard')}
            className={styles.cancelButton}
            disabled={isSubmitting}
          >
            Back to Dashboard
          </button>
        </div>
      </form>

      <div className={styles.requestList}>
        <h3>Your Previous Requests</h3>
        {requests.length === 0 ? (
          <p>No requests found</p>
        ) : (
          <table className={styles.requestsTable}>
            <thead>
              <tr>
                <th>Service</th>
                <th>Description</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {requests.map(request => {
                const service = services.find(s => s.serviceId === request.serviceId);
                return (
                  <tr key={request.requestId}>
                    <td>{service?.serviceName || 'Unknown'}</td>
                    <td>{request.description}</td>
                    <td className={styles[request.status.toLowerCase()]}>
                      {request.status}
                    </td>
                    <td>
                      {request.status === 'Pending' && (
                        <>
                          <button 
                            onClick={() => handleEdit(request)}
                            className={styles.editButton}
                          >
                            Edit
                          </button>
                          <button 
                            onClick={() => handleDelete(request.requestId)}
                            className={styles.deleteButton}
                          >
                            Delete
                          </button>
                        </>
                      )}
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default RaiseRequest;
