import axios from 'axios'
import React, { useState } from 'react'

const EmployeeInput = () => {
    const [employee,setEmployee]= useState({
        id:"",
        name:"",
        dept:"",
        salary:""
    })
    const handleChange = (e)=>{
        setEmployee({...employee,[e.target.name]:e.target.value})
    }
    const handleSubmit = (e) =>{
        axios.post("http://localhost:8080/employee/emp",employee).then((Response)=>{
            alert("Submitted");
            console.log();
            
            setEmployee({
                id:"",
                name:"",
                dept:"",
                salary:""

            })
        })
    }
  return (
    <>
        <form action="" style={{margin:'10%', border: '2px solid black',padding:'2% 2% 2% 30%'}}>
        <h1>Employee's Detail</h1> <br />
        <input type="number" name="id" id="id" placeholder='Enter Id' value={employee.id}onChange={handleChange}/><br /> <br/>
        <input type="text" name="name" id="name" placeholder='Enter name' value={employee.name} onChange={handleChange}/><br /> <br />
        <input type="text" name="dept" id="dept" placeholder='Enter department' value={employee.dept} onChange={handleChange} /> <br /><br />
        <input type="number" name="salary" id="salary" placeholder='Enter salary'value={employee.salary} onChange={handleChange} />
        <button type='submit' style={{marginLeft:'10%'}} onClick={handleSubmit}>Submit</button>
    </form>
    </>
    
  )
}

export default EmployeeInput
