import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'

const StudentForm = () => {
    const navigate = useNavigate();
    const [form,setForm]= useState({
        name:'',
        roll:'',
        dept:'',
        semCOunt:''
    })
    const handleChange =(e)=>{
        setForm({...form,[e.target.name]:e.target.value})
    }

    const handleSubmit =(e)=>{
        e.preventDefault();
        console.log(form);
        setForm({
          name: "",
          roll: "",
          dept: "",
          semCOunt: "",
        });
        navigate("/subject")
    }
  return (
    <>
    <form >
        <input type="text" name="name" id="name" placeholder='Student Name' value={form.name} onChange={handleChange} required/>
        <input type="number" name="roll" id="roll" placeholder='Register Number' value={form.roll} onChange={handleChange} required/>
        <input type="text" name="dept" id="dept" placeholder='Department' value={form.dept} onChange={handleChange} required/>
        <input type="number" name="semCount" id="semCount" placeholder='No of Semesters' value={form.semCount} onChange={handleChange} required/>
        <button onClick={handleSubmit}>Next</button>
    </form>
    </>  
  )
}

export default StudentForm
