import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './StudentForm.module.css'; // Import CSS Module

const StudentForm = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    name: '',
    roll: '',
    dept: '',
    semCount: ''
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    localStorage.setItem('studentForm', JSON.stringify(form));
    setForm({
      name: '',
      roll: '',
      dept: '',
      semCount: ''
    });
    navigate('/subject');
  };

  return (
    <div className={styles.pageContainer}>
      <h1 className={styles.mainHeading}>Student Grading System</h1> 
      <div className={styles.formContainer}>
        <h2 className={styles.formHeading}>Student Information</h2>
        <form onSubmit={handleSubmit} className={styles.form}>
          <div className={styles.formGroup}>
            <label className={styles.label}>Student Name</label>
            <input
              className={styles.input}
              type="text"
              name="name"
              value={form.name}
              onChange={handleChange}
              required
            />
          </div>
          <div className={styles.formGroup}>
            <label className={styles.label}>Register Number</label>
            <input
              className={styles.input}
              type="number"
              name="roll"
              value={form.roll}
              onChange={handleChange}
              required
            />
          </div>
          <div className={styles.formGroup}>
            <label className={styles.label}>Department</label>
            <input
              className={styles.input}
              type="text"
              name="dept"
              value={form.dept}
              onChange={handleChange}
              required
            />
          </div>
          <div className={styles.formGroup}>
            <label className={styles.label}>No of Semesters</label>
            <input
              className={styles.input}
              type="number"
              name="semCount"
              value={form.semCount}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className={styles.button}>Next</button>
        </form>
      </div>
    </div>
  );
};

export default StudentForm;
