import React, { useState } from 'react'

const Input = () => {
    const [user,setUser] = useState({
        id:"",
        name:"",
        dept:""
    })
    const handleChange = (e)=>{
        setUser({...user,[e.target.id]:e.target.value});
    }
    const handleSubmit = (e)=>{
        e.preventDefault();
        alert("Form submitted");
        setUser({
            id:"",
            name:"",
            dept:""

        })
    }
  return (
    <>
    <form action="" style={{margin:'10%', border: '2px solid black',padding:'2% 2% 2% 30%'}}>
        <h1>Student's Detail</h1> <br />
        <input type="number" name="id" id="id" placeholder='Enter Id' value={user.id}onChange={handleChange}/><br /> <br/>
        <input type="text" name="name" id="name" placeholder='Enter name' value={user.name} onChange={handleChange}/><br /> <br />
        <input type="text" name="department" id="department" placeholder='Enter department' value={user.dept} onChange={handleChange} /> <br /><br />
        <button type='submit' style={{marginLeft:'10%'}} onSubmit={handleSubmit}>Submit</button>
    </form>
    </>
  )
}

export default Input
