import React, { useState } from 'react';
import styles from './ManageRequest.module.css';

const dummyRequests = [
  { id: 1, user: 'John Doe', service: 'Plumbing', description: 'Leak in kitchen', status: 'Pending' },
  { id: 2, user: 'Jane Smith', service: 'Electricity', description: 'Power cut', status: 'In Progress' },
];

const ManageRequest = () => {
  const [requests, setRequests] = useState(dummyRequests);
  const [filter, setFilter] = useState('All');
  setRequests();

  const filteredRequests = filter === 'All'
    ? requests
    : requests.filter(req => req.status === filter);

  return (
    <div className={styles.requests}>
      <h3>Manage Requests</h3>
      <label>Filter by Status: </label>
      <select value={filter} onChange={(e) => setFilter(e.target.value)}>
        <option>All</option>
        <option>Pending</option>
        <option>In Progress</option>
        <option>Completed</option>
      </select>
      <ul>
        {filteredRequests.map((req) => (
          <li key={req.id}>
            <strong>{req.service}</strong> - {req.description} - {req.status} - <button onClick={() => alert(JSON.stringify(req, null, 2))}>View</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageRequest;
