import React, { useState } from 'react';
import styles from './Login.module.css';

const Login = () => {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('');
  const handleChange = (e) => {
    setName({ ...name, [e.target.name]: e.target.value });
    setName({ ...password, [e.target.password]: e.target.value });
    setName({ ...role, [e.target.role]: e.target.value });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Login Details:', { username: name, password, role });
    alert("Form submitted successfully!");
    setName('');
    setPassword('');
    setRole('');
  };

  return (
    <>
      <h1 className={styles.head}>My Colony</h1>
      <form className={styles.login} onSubmit={handleSubmit}>
        <h1>Login</h1>
        <input type="text" placeholder="Username" value={name.name} onChange={(e) => setName(e.target.value)}required/>
        <input type="password" placeholder="Password" value={password.password} onChange={(e) => setPassword(e.target.value)}required/>
        <select value={role.role}
        
        
        
        
        
        
        
        
        
        
        
        
        
        
        
        onChange={(e) => setRole(e.target.value)}required>
          <option value="">Select Role</option>
          <option value="Admin">Admin</option>
          <option value="Super Admin">Super Admin</option>
          <option value="User">User</option>
        </select>
        <a href="register">Forgot password?</a>
        <button type="submit">Log in</button>
        <a href="new">Create new account</a>
      </form>
    </>
  );
};

export default Login;
