import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import styles from "./EditRequest.module.css";

const EditRequest = () => {
  const { id } = useParams();              // /edit-request/:id
  const navigate = useNavigate();

  const [form, setForm] = useState({
    serviceName: "",
    serviceId: "",
    userId: "",
    description: "",
    status: "",
    createDate: "",
    exceptCompletionDate: "",
  });

  const [message, setMessage] = useState("");
  const [isError, setIsError] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // ðŸ”¹ load request by id
  useEffect(() => {
    axios
      .get(`http://localhost:8080/requests/${id}`)
      .then((res) => {
        const data = res.data;

        setForm({
          serviceName: data.serviceName || "",      // if you donâ€™t have, remove
          serviceId: data.serviceId,
          userId: data.userId,
          description: data.description || "",
          status: data.status || "Pending",
          createDate: data.createDate || "",
          exceptCompletionDate: data.exceptCompletionDate || "",
        });
      })
      .catch((err) => {
        console.error("Error loading request:", err);
        setMessage("Failed to load request details.");
        setIsError(true);
      });
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!form.description.trim()) {
      setIsError(true);
      setMessage("Description cannot be empty");
      return;
    }

    setIsSubmitting(true);

    const payload = {
      requestId: parseInt(id),
      serviceId: form.serviceId,
      userId: form.userId,
      description: form.description.trim(),
      status: form.status,
      createDate: form.createDate,
      exceptCompletionDate: form.exceptCompletionDate,
    };

    axios
      .put(`http://localhost:8080/requests/update/${id}`, payload)
      .then((res) => {
        setIsError(false);
        setMessage("Request updated successfully");
        setTimeout(() => {
          navigate(`/requests/${id}`);   // RequestDetail route
        }, 1500);
      })
      .catch((err) => {
        console.error("Error updating request:", err);
        setIsError(true);
        setMessage(
          err.response?.data?.message || "Update failed. Please try again."
        );
      })
      .finally(() => {
        setIsSubmitting(false);
      });
  };

  return (
    <div className={styles.container}>
      <h2>Edit Request</h2>

      {message && (
        <div className={isError ? styles.error : styles.success}>{message}</div>
      )}

      <form onSubmit={handleSubmit} className={styles.form}>
        <div className={styles.formGroup}>
          <label>Service Type:</label>
          <input
            type="text"
            value={form.serviceName}
            disabled
            className={styles.input}
          />
        </div>

        <div className={styles.formGroup}>
          <label>Description:</label>
          <textarea
            name="description"
            value={form.description}
            onChange={handleChange}
            className={styles.textarea}
          />
        </div>

        <div className={styles.formGroup}>
          <label>Status:</label>
          <select
            name="status"
            value={form.status}
            onChange={handleChange}
            className={styles.select}
          >
            <option value="Pending">Pending</option>
            <option value="In-Progress">In-Progress</option>
            <option value="Completed">Completed</option>
          </select>
        </div>

        <div className={styles.formGroup}>
          <label>Create Date:</label>
          <input
            type="text"
            value={form.createDate}
            disabled
            className={styles.input}
          />
        </div>

        <div className={styles.formGroup}>
          <label>Expect Completion Date:</label>
          <input
            type="text"
            name="exceptCompletionDate"
            value={form.exceptCompletionDate || ""}
            onChange={handleChange}
            className={styles.input}
          />
        </div>

        <div className={styles.buttonGroup}>
          <button
            type="submit"
            className={styles.saveButton}
            disabled={isSubmitting}
          >
            {isSubmitting ? "Saving..." : "Save Changes"}
          </button>
          <button
            type="button"
            className={styles.cancelButton}
            onClick={() => navigate(-1)}
            disabled={isSubmitting}
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditRequest;
