import React, { useState } from 'react';
import styles from './ManageUsers.module.css';

const initialUsers = [
  { flat: '101', name: 'John Doe', contact: '1234567890' },
  { flat: '102', name: 'Jane Smith', contact: '9876543210' },
];

const ManageUsers = () => {
  const [users, setUsers] = useState(initialUsers);
  const [newUser, setNewUser] = useState({ flat: '', name: '', contact: '' });

  const handleChange = (e) => {
    setNewUser({ ...newUser, [e.target.name]: e.target.value });
  };

  const handleAddUser = (e) => {
    e.preventDefault();
    if (newUser.flat && newUser.name && newUser.contact) {
      setUsers([...users, newUser]);
      setNewUser({ flat: '', name: '', contact: '' });
    }
  };

  const handleDeleteUser = (flat) => {
    const updatedUsers = users.filter(user => user.flat !== flat);
    setUsers(updatedUsers);
  };

  return (
    <div className={styles.userList}>
      <h3>Manage Users</h3>
      <ul>
        {users.map((user, index) => (
          <li key={index} className={styles.userItem}>
            {user.flat} - {user.name} - {user.contact}
            <button
              onClick={() => handleDeleteUser(user.flat)}
              className={styles.deleteButton}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>

      <form onSubmit={handleAddUser} className={styles.userForm}>
        <h4>Add New User</h4>
        <input
          type="text"
          name="flat"
          placeholder="Flat No"
          value={newUser.flat}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={newUser.name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="contact"
          placeholder="Contact"
          value={newUser.contact}
          onChange={handleChange}
          required
        />
        <button type="submit" className={styles.addButton}>Add User</button>
      </form>
    </div>
  );
};

export default ManageUsers;
