import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './RaiseRequest.module.css';

const RaiseRequest = () => {
  const [serviceId, setServiceId] = useState('');
  const [description, setDescription] = useState('');
  const [message, setMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const userId = localStorage.getItem('userId');

  // Fetch available services
  useEffect(() => {
    axios.get('http://localhost:8080/services/data')
      .then(response => {
        setServices(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching services:', error);
        setLoading(false);
      });
  }, []);

  const handleSubmit = () => {
    if (!serviceId || !description) {
      setMessage('Please fill all fields');
      setIsError(true);
      setTimeout(() => setMessage(''), 3000);
      return;
    }

    const newRequest = {
      serviceId: parseInt(serviceId),
      userId: parseInt(userId),
      description: description,
      status: "Pending",
      createDate: new Date().toISOString(),
      exceptCompletionDate: new Date(Date.now() + 86400000).toISOString()
    };

    axios.post('http://localhost:8080/requests/save', newRequest)
      .then(response => {
        setServiceId('');
        setDescription('');
        setIsError(false);
        setMessage('Request submitted successfully!');
        setTimeout(() => setMessage(''), 3000);
      })
      .catch(error => {
        console.error("Error submitting request:", error);
        setMessage('Something went wrong. Try again.');
        setIsError(true);
        setTimeout(() => setMessage(''), 3000);
      });
  };

  if (loading) return <div className={styles.loading}>Loading services...</div>;

  return (
    <div className={styles.requestForm}>
      <h3>Raise Request</h3>

      <select
        value={serviceId}
        onChange={(e) => setServiceId(e.target.value)}
        className={styles.input}
        required
      >
        <option value="">Select Service</option>
        {services.map(service => (
          <option key={service.serviceId} value={service.serviceId}>
            {service.serviceName}
          </option>
        ))}
      </select>

      <textarea
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        className={styles.textarea}
        required
      />

      <button onClick={handleSubmit} className={styles.submitButton}>
        Submit
      </button>

      {message && (
        <p className={isError ? styles.errorMsg : styles.successMsg}>
          {message}
        </p>
      )}

      <button onClick={() => navigate('/userdashboard')} className={styles.backBtn}>
        Back to Dashboard
      </button>
    </div>
  );
};

export default RaiseRequest;
