import React, { useEffect, useState } from 'react';
import { useLocation, useParams } from 'react-router-dom';
import styles from './RequestDetail.module.css';
import axios from 'axios';

const RequestDetail = () => {
  const [request, setRequest] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { id } = useParams();
  const location = useLocation();

  useEffect(() => {
    if (location.state?.request) {
      setRequest(location.state.request);
      setLoading(false);
    } else if (id) {
      axios.get(`http://localhost:8080/requests/${id}`)
        .then(response => {
          setRequest(response.data);
          setLoading(false);
        })
        .catch(error => {
          console.error('Error fetching request:', error);
          setError(error.message);
          setLoading(false);
        });
    } else {
      setLoading(false);
    }
  }, [id, location.state]);

  if (loading) return <div className={styles.detailContainer}>Loading...</div>;
  if (error) return <div className={styles.detailContainer}>Error: {error}</div>;
  if (!request) return <div className={styles.detailContainer}>No request found</div>;

  return (
    <div className={styles.detailContainer}>
      <h3>Request Detail</h3>
      <p><strong>Request ID:</strong> {request.requestId}</p>
      <p><strong>Service ID:</strong> {request.serviceId}</p>
      <p><strong>User ID:</strong> {request.userId}</p>
      <p><strong>Description:</strong> {request.description}</p>
      <p><strong>Status:</strong> {request.status}</p>
      <p><strong>Created Date:</strong> {request.createDate}</p>
    </div>
  );
};

export default RequestDetail;
