import React, { useState } from "react";

function RaiseRequest() {
  const [service, setService] = useState("");
  const [description, setDescription] = useState("");
  const [message, setMessage] = useState("");

     const handleChange =(f) =>{
        setService({...service,[f.target.service]:f.target.value})
    }

  const handleSubmit = () => {
    if (!service) setMessage("Please select a service type");
    else if (!description) setMessage("Please enter a description");
    else setMessage("Request submitted successfully!");
    setService({
        service:""
    })
    setDescription({
        description:""
    })

  };

  return (
    <div className="form-container">
      <h2>Raise Service Request</h2>
      <select onChange={handleChange} value={service.service}>
        <option value="">Select Service</option>
        <option value="plumbing">Plumbing</option>
        <option value="electricity">Electricity</option>
      </select>
      <textarea placeholder="Description" onChange={handleChange} value={description.description} />
      <button onClick={handleSubmit}>Submit</button>
      <p className="message">{message}</p>
    </div>
  );
}

export default RaiseRequest;
