import React, { useState } from 'react';
import axios from 'axios';
import styles from './Forgot.module.css';

const Forgot = () => {
  const [email, setEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");

  const handleSubmit = (e) => {
  e.preventDefault();

  const cleanedEmail = email.trim().toLowerCase(); // ✅ Trim and clean the email

  if (!cleanedEmail || !newPassword.trim()) {
    alert("Both email and new password are required");
    return;
  }

  axios.put(`http://localhost:8080/users/forgot-password/${cleanedEmail}`, {
    newPassword: newPassword.trim(), // ✅ Clean the password
  })
  .then((res) => {
    alert(res.data);
    setEmail("");
    setNewPassword("");
  })
  .catch((err) => {
    if (err.response && err.response.data) {
      alert(`Error: ${err.response.data}`);
    } else {
      alert("Something went wrong!");
    }
  });
};

  return (
    <div className={styles.forgotContainer}>
      <h2>Reset Password</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="email"
          placeholder="Enter email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className={styles.forgotInput}
          required
        />
        <input
          type="password"
          name="newPassword"
          placeholder="Enter new password"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
          className={styles.forgotInput}
          required
        />
        <button type="submit" className={styles.forgotButton}>Submit</button>
      </form>
    </div>
  );
};

export default Forgot;
