import React, { useState } from 'react'

const Form2 = () => {
    const [input,setInput]=useState({
        name:"",
        age:""
    })
    const handleChange =(f) =>{
        setInput({...input,[f.target.name]:f.target.value})
    }

    const handleSubmit =() =>{
        console.log(input);
        
    }
  return (
    <>
    <h1>Application Form</h1>
    
    <label htmlFor="name">Name</label>
    <input type="text" name="name" id="name" required value={input.name} onChange={handleChange} />

    <label htmlFor="age">Age</label>
    <input type="number" name="age" id="age" required onChange={handleChange}/>

    <button type="submit" value={handleSubmit}>Submit</button>

    </>
  )
}

export default Form2
