import React, { useEffect, useState, useRef } from "react";
import html2canvas from "html2canvas";
import styles from "./Result.module.css"; // Import CSS module

const Result = () => {
  const [data, setData] = useState(null);
  const resultRef = useRef();

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("resultData"));
    if (stored) {
      setData(stored);
    }
  }, []);

  const downloadImage = () => {
    html2canvas(resultRef.current).then((canvas) => {
      const link = document.createElement("a");
      link.download = "CGPA_Result.png";
      link.href = canvas.toDataURL("image/png");
      link.click();
    });
  };

  if (!data) return <h2 className={styles.noData}>No result data found</h2>;

  return (
    <div className={styles.page}>
      <div ref={resultRef} className={styles.card}>
        <h2 className={styles.title}>Student CGPA Report</h2>
        <div className={styles.details}>
          <p><strong>Name:</strong> {data.student.name}</p>
          <p><strong>Roll Number:</strong> {data.student.roll}</p>
          <p><strong>Department:</strong> {data.student.dept}</p>
        </div>

        <h3 className={styles.subTitle}>Semester-wise CGPA</h3>
        <ul className={styles.semList}>
          {data.semesterResults.map((sem) => (
            <li key={sem.semesterNumber}>
              Semester {sem.semesterNumber}: <span>{sem.cgpa}</span>
            </li>
          ))}
        </ul>

        <h3 className={styles.overall}>Overall CGPA: {data.overallCgpa}</h3>
      </div>

      <button onClick={downloadImage} className={styles.button}>
        Download as Image
      </button>
    </div>
  );
};

export default Result;
