import React, { useState } from 'react';
import styles from './ManageRequest.module.css';

const dummyRequests = [
   { id: 101, user: 'John Doe', service: 'Plumbing', description: 'Leak in kitchen', status: 'Pending' },
  { id: 102, user: 'Jane Smith', service: 'Electricity', description: 'Power cut', status: 'In Progress' },
  { id: 103, user: 'Akash', service: 'Garbage', description: 'Trash not collected', status: 'Pending' },
  { id: 104, user: 'Arya Krishna', service: 'Maintenance', description: 'Broken gate', status: 'Completed' },
];

const ManageRequest = () => {
  const [requests, setRequests] = useState(dummyRequests);

  const handleStatusUpdate = (id, newStatus) => {
    const updatedRequests = requests.map((req) =>
      req.id === id ? { ...req, status: newStatus } : req
    );
    setRequests(updatedRequests);
  };

  return (
    <div className={styles.container}>
      <h2 className={styles.heading}>Manage Requests</h2>
      {requests.map((req) => (
        <div key={req.id} className={styles.card}>
          <h3>{req.user}</h3>
          <p><strong>Service:</strong> {req.service}</p>
          <p><strong>Description:</strong> {req.description}</p>
          <p><strong>Status:</strong> {req.status}</p>
          <button
            className={styles.button}
            onClick={() => handleStatusUpdate(req.id, 'Completed')}
            disabled={req.status === 'Completed'}
          >
            Mark as Completed
          </button>
        </div>
      ))}
    </div>
  );
};

export default ManageRequest;
