import React, { useEffect, useState } from 'react';
import styles from './ViewRequest.module.css';
import { useNavigate } from 'react-router-dom';

const ViewRequest = ({ userId }) => {
  const [requests, setRequests] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`http://localhost:8080/api/requests/user/${userId}`)
      .then(response => response.json())
      .then(data => setRequests(data))
      .catch(error => console.error('Error fetching requests:', error));
  }, [userId]);

  const handleSubmit = (selectedRequest) => {
    navigate('/requestdetail', { state: { request: selectedRequest } });
  };

  return (
    <div className={styles.container}>
      <h3 className={styles.heading}>View My Requests</h3>
      {requests.length === 0 ? (
        <p className={styles.noRequests}>No requests yet.</p>
      ) : (
        <ul className={styles.list}>
          {requests.map((req) => (
            <li key={req.requestId} className={styles.listItem}>
              <strong>Service ID: {req.serviceId}</strong> - {req.description}
              <br />
              <button type="button" onClick={() => handleSubmit(req)}>
                View Request Detail
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ViewRequest;
