import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const EditForm = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const student = location.state?.student;

  const [form, setForm] = useState({
    name: '',
    dept: ''
  });

  useEffect(() => {
    if (student) {
      setForm({
        name: student.name,
        dept: student.dept
      });
    }
  }, [student]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleUpdate = (e) => {
    e.preventDefault();
    axios
      .put(`http://localhost:8080/student/update/${student.id}`, form)
      .then((response) => {
        console.log('Updated:', response.data);
        navigate('/apis');
      })
      .catch((error) => {
        console.error('Update error:', error);
      });
  };

  return (
    <>
      <form onSubmit={handleUpdate}>
        <input type="text" name="name" value={form.name} onChange={handleChange}placeholder="Enter Name"/>
        <input type="text"name="dept" value={form.dept} onChange={handleChange} placeholder="Enter Department"/>
        <button type="submit">Update</button>
      </form>
    </>
  );
};

export default EditForm;
