import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import styles from './EditRequest.module.css';

const EditRequest = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const [formData, setFormData] = useState({
    serviceId: '',
    userId: '',
    description: '',
    status: '',
    createDate: '',
    exceptCompletionDate: ''
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // If coming from RequestDetail with state
    if (location.state?.request) {
      setFormData(location.state.request);
      setLoading(false);
    } else {
      // Fetch request if directly accessing the edit page
      axios.get(`http://localhost:8080/requests/${id}`)
        .then(response => {
          setFormData(response.data);
          setLoading(false);
        })
        .catch(error => {
          console.error('Error fetching request:', error);
          setError(error.response?.data?.message || error.message);
          setLoading(false);
        });
    }
  }, [id, location.state]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    
    axios.put(`http://localhost:8080/requests/${formData.id}`, formData)
      .then(response => {
        alert('Request updated successfully!');
        navigate(`/requests/${id}`);
      })
      .catch(error => {
        console.error('Error updating request:', error);
        setError(error.response?.data?.message || error.message);
      })
      .finally(() => {
        setLoading(false);
      });
  };

  if (loading) return <div className={styles.editContainer}>Loading...</div>;
  if (error) return <div className={styles.editContainer}>Error: {error}</div>;

  return (
    <div className={styles.editContainer}>
      <h2>Edit Request</h2>
      <form onSubmit={handleSubmit} className={styles.editForm}>
        <div className={styles.formGroup}>
          <label>Service Type:</label>
          <select
            name="serviceId"
            value={formData.serviceId}
            onChange={handleChange}
            required
          >
            <option value="">Select Service</option>
            <option value="1">Plumbing</option>
            <option value="2">Electricity</option>
            <option value="3">Garbage Collection</option>
            <option value="4">Maintenance</option>
            <option value="5">Cleaning</option>
            <option value="6">Security</option>
          </select>
        </div>

        <div className={styles.formGroup}>
          <label>User ID:</label>
          <input
            type="number"
            name="userId"
            value={formData.userId}
            onChange={handleChange}
            required
            disabled // Typically you wouldn't allow changing user ID
          />
        </div>

        <div className={styles.formGroup}>
          <label>Description:</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            required
            rows="4"
          />
        </div>

        <div className={styles.formGroup}>
          <label>Status:</label>
          <select
            name="status"
            value={formData.status}
            onChange={handleChange}
            required
          >
            <option value="">Select Status</option>
            <option value="Pending">Pending</option>
            <option value="InProgress">In Progress</option>
            <option value="Completed">Completed</option>
            <option value="Cancelled">Cancelled</option>
          </select>
        </div>

        <div className={styles.formGroup}>
          <label>Created Date:</label>
          <input
            type="datetime-local"
            name="createDate"
            value={formData.createDate ? new Date(formData.createDate).toISOString().slice(0, 16) : ''}
            onChange={handleChange}
            disabled // Typically creation date shouldn't be editable
          />
        </div>

        <div className={styles.formGroup}>
          <label>Expected Completion:</label>
          <input
            type="datetime-local"
            name="exceptCompletionDate"
            value={formData.exceptCompletionDate ? new Date(formData.exceptCompletionDate).toISOString().slice(0, 16) : ''}
            onChange={handleChange}
          />
        </div>

        <div className={styles.buttonGroup}>
          <button type="submit" disabled={loading}>
            {loading ? 'Updating...' : 'Update Request'}
          </button>
          <button type="button" onClick={() => navigate(-1)}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditRequest;
