import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styles from './ManageRequests.module.css';

const ManageRequests = () => {
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRequests = async () => {
      try {
        const response = await axios.get('http://localhost:8080/requests/get');
        setRequests(response.data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchRequests();
  }, []);

  const updateStatus = async (requestId, newStatus) => {
    try {
      await axios.put(`http://localhost:8080/requests/${requestId}/status`, null, {
        params: { status: newStatus }
      });
      setRequests(requests.map(req => 
        req.requestId === requestId ? { ...req, status: newStatus } : req
      ));
    } catch (err) {
      console.error('Error updating status:', err);
      alert('Failed to update status');
    }
  };

  if (loading) return <div className={styles.container}>Loading requests...</div>;
  if (error) return <div className={styles.container}>Error: {error}</div>;

  return (
    <div className={styles.container}>
      <h2>Manage Requests</h2>
      <table className={styles.requestTable}>
        <thead>
          <tr>
            <th>Request ID</th>
            <th>User ID</th>
            <th>Service ID</th>
            <th>Description</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {requests.map((request) => (
            <tr key={request.requestId}>
              <td>{request.requestId}</td>
              <td>{request.userId}</td>
              <td>{request.serviceId}</td>
              <td>{request.description}</td>
              <td className={styles[request.status.toLowerCase()]}>
                {request.status}
              </td>
              <td>
                {request.status !== 'Completed' && (
                  <button
                    onClick={() => updateStatus(request.requestId, 'Completed')}
                    className={styles.completeButton}
                  >
                    Mark Completed
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageRequests;
