import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './ManageUsers.module.css';

const ManageUsers = () => {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({ 
    userName: '', 
    email: '', 
    mobileNo: '', 
    block: '', 
    floor: '', 
    doorNo: '', 
    role: 'User', 
    password: 'default123' 
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [mobileError, setMobileError] = useState('');

  
  useEffect(() => {
    axios.get('http://localhost:8080/users/data')
      .then(response => {
        setUsers(response.data);
        setLoading(f)
      })
      .catch(error => {
        setError('Failed to load users. Please try again.');
        console.error('Erro fetching users:', error);
      });
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'mobileNo') {
      if (value === '' || (/^\d*$/.test(value) && value.length <= 10)) {
        setNewUser({ ...newUser, [name]: value });
        if (value && value.length !== 10) {
          setMobileError('Mobile number must be 10 digits');
        } else {
          setMobileError('');
        }
      }
    } else {
      setNewUser({ ...newUser, [name]: value });
    }
  };

  const handleAddUser = (e) => {
    e.preventDefault();
    if (newUser.mobileNo.length !== 10) {
      setMobileError('Mobile number must be 10 digits');
      return;
    }
    const userData = {
      ...newUser,
      mobileNo: parseInt(newUser.mobileNo),
      floor: parseInt(newUser.floor),
      doorNo: parseInt(newUser.doorNo)
    };

    axios.post('http://localhost:8080/users/save', userData)
      .then(response => {
        setUsers([...users, response.data]);
        setNewUser({ 
          userName: '', 
          email: '', 
          mobileNo: '', 
          block: '', 
          floor: '', 
          doorNo: '', 
          role: 'User', 
          password: 'default123' 
        });
        setMobileError('');
        setSuccess('User added successfully!');
        setTimeout(() => setSuccess(''), 3000);
      })
      .catch(error => {
        setError('Failed to add user. Please try again.');
        console.error('Error adding user:', error);
      });
  };

  const handleDeleteUser = (userId) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      axios.delete(`http://localhost:8080/users/delete/${userId}`)
        .then(() => {
          setUsers(users.filter(user => user.userId !== userId));
          setSuccess('User deleted successfully!');
          setTimeout(() => setSuccess(''), 3000);
        })
        .catch(error => {
          setError('Failed to delete user. Please try again.');
          console.error('Error deleting user:', error);
        });
    }
  };

  if (loading) return <div className={styles.loading}>Loading users...</div>;
  if (error) return <div className={styles.error}>{error}</div>;

  return (
    <div className={styles.container}>
      <h2>Manage Users</h2>
      
      {success && <div className={styles.success}>{success}</div>}

      <table className={styles.userTable}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Contact</th>
            <th>Address</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.userId}>
              <td>{user.userId}</td>
              <td>{user.userName}</td>
              <td>{user.email}</td>
              <td>{user.mobileNo}</td>
              <td>{user.block}-{user.floor}-{user.doorNo}</td>
              <td>{user.role}</td>
              <td>
                <button 
                  onClick={() => handleDeleteUser(user.userId)}
                  className={styles.deleteBtn}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add User Form */}
      <form onSubmit={handleAddUser} className={styles.userForm}>
        <h3>Add New User</h3>
        
        <div className={styles.formGroup}>
          <label>Full Name:</label>
          <input
            type="text"
            name="userName"
            value={newUser.userName}
            onChange={handleChange}
            required
          />
        </div>
        
        <div className={styles.formGroup}>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={newUser.email}
            onChange={handleChange}
            required
          />
        </div>
        
        <div className={styles.formGroup}>
          <label>Mobile:</label>
          <input
            type="text"
            name="mobileNo"
            value={newUser.mobileNo}
            onChange={handleChange}
            required
            maxLength="10"
          />
          {mobileError && <span className={styles.errorText}>{mobileError}</span>}
        </div>
        
        <div className={styles.addressGroup}>
          <div className={styles.formGroup}>
            <label>Block:</label>
            <select
              name="block"
              value={newUser.block}
              onChange={handleChange}
              required
            >
              <option value="">Select</option>
              <option value="A">A</option>
              <option value="B">B</option>
              <option value="C">C</option>
              <option value="D">D</option>
              <option value="F">F</option>
            </select>
          </div>
          
          <div className={styles.formGroup}>
            <label>Floor:</label>
            <select
              name="floor"
              value={newUser.floor}
              onChange={handleChange}
              required
            >
              <option value="">Select</option>
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
              <option value="5">5</option>
            </select>
          </div>
          
          <div className={styles.formGroup}>
            <label>Door No:</label>
            <input
              type="number"
              name="doorNo"
              value={newUser.doorNo}
              onChange={handleChange}
              required
            />
          </div>
        </div>
        
        <div className={styles.formGroup}>
          <label>Role:</label>
          <select
            name="role"
            value={newUser.role}
            onChange={handleChange}
            required
          >
            <option value=""></option>
            <option value="User">User</option>
            
          </select>
        </div>
        
        <button 
          type="submit" 
          className={styles.addBtn}
          disabled={mobileError} // Disable if there's a mobile error
        >
          Add User
        </button>
      </form>
    </div>
  );
};

export default ManageUsers;
