import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Users = () => {
  const [input, setInput] = useState([]);
  const [editItem, setEditItem] = useState(null);
  const [editedData, setEditedData] = useState({ name: '', dept: '' });

  const handleSubmit = () => {
    axios.get("http://localhost:8080/student/data")
      .then((Response) => {
        setInput(Response.data);
      })
  };

  useEffect(() => {
    handleSubmit();
  }, []);

  const handleEdit = (item) => {
    setEditItem(item);
    setEditedData({ name: item.name, dept: item.dept });
  };

  const saveEdit = (id) => {
    axios.put(`http://localhost:8080/student/update/${id}`, editedData)
      .then((Response) => {
        console.log("Updated:", Response.data);
        setEditItem(null);     
        handleSubmit();       
      })
  };

  return (
    <>
      <h2>Student Table</h2>
      <table border={2} >
        <thead>
          <tr>
            <th>S.No</th>
            <th>Id</th>
            <th>Name</th>
            <th>Department</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {input.map((item, index) => (
            <tr key={item.id}>
              <td>{index + 1}</td>
              <td>{item.id}</td>
              <td>
                {editItem?.id === item.id ? (
                  <input type="text" value={editedData.name}onChange={(e) => setEditedData({ ...editedData, name: e.target.value })}/>) : (item.name)}
              </td>
              <td>
                {editItem?.id === item.id ? (
                  <input
                    type="text"
                    value={editedData.dept}onChange={(e) => setEditedData({ ...editedData, dept: e.target.value })}
                  />
                ) : (
                  item.dept
                )}
              </td>
              <td>
                {editItem?.id === item.id ? (
                  <button onClick={() => saveEdit(item.id)}>Save</button>
                ) : (
                  <button onClick={() => handleEdit(item)}>Edit</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default Users;
