import React, { useState } from 'react';
import styles from './RaiseRequest.module.css';

const RaiseRequest = ({ addRequest }) => {
  const [service, setService] = useState('');
  const [description, setDescription] = useState('');
  const [message, setMessage] = useState('');
  const [isError, setIsError] = useState(false);

  const handleSubmit = () => {
    if (!service || !description) {
      setMessage('Please fill all fields');
      setIsError(true); // error state
      return;
    }

    const newRequest = {
      service,
      description,
      status: 'Pending',
    };

    addRequest(newRequest);
    setService('');
    setDescription('');
    setMessage('Request submitted!');
    setIsError(false); // success state
  };

  return (
    <div className={styles.requestForm}>
      <h3>Raise Request</h3>

      <select value={service} onChange={(e) => setService(e.target.value)}>
        <option value="">Select Service</option>
        <option value="Plumbing">Plumbing</option>
        <option value="Electricity">Electricity</option>
      </select>
      <br />

      <textarea
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <br />

      <button onClick={handleSubmit}>Submit</button>

      {/* Show message under the button */}
      <p
        className={isError ? styles.errorMsg : styles.successMsg}
      >
        {message}
      </p>
    </div>
  );
};

export default RaiseRequest;
