import React, { useEffect, useState } from 'react';
import { useLocation, useParams } from 'react-router-dom';
import styles from './RequestDetail.module.css';
import axios from 'axios';

const RequestDetail = () => {
  const [request, setRequest] = useState(null);
  const { id } = useParams();
  const location = useLocation();

  useEffect(() => {
    if (location.state?.request) {
      setRequest(location.state.request);
    } else if (id) {
      axios.get(`http://localhost:8080/api/requests/${id}`)
        .then(response => setRequest(response.data))
        .catch(error => console.error('Error fetching request:', error));
    }
  }, [id, location.state]);

  if (!request) return <p>Loading request details...</p>;

  return (
    <div className={styles.detailContainer}>
      <h3>Request Detail</h3>
      <p><strong>Request ID:</strong> {request.requestId}</p>
      <p><strong>Service ID:</strong> {request.serviceId}</p>
      <p><strong>User ID:</strong> {request.userId}</p>
      <p><strong>Description:</strong> {request.description}</p>
      <p><strong>Status:</strong> {request.status}</p>
      <p><strong>Created Date:</strong> {request.createDate}</p>
    </div>
  );
};

export default RequestDetail;
