import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './RequestDetail.module.css';

const RequestDetail = () => {
  const { id } = useParams();
  const [request, setRequest] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchRequest = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/requests/${id}`);
        setRequest(response.data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchRequest();
  }, [id]);

  if (loading) return <div className={styles.container}>Loading...</div>;
  if (error) return <div className={styles.container}>Error: {error}</div>;
  if (!request) return <div className={styles.container}>No request found</div>;

  return (
    <div className={styles.detailContainer}>
      <h3>Request Details</h3>
      <div className={styles.detailItem}>
        <span>Request ID:</span>
        <span>{request.requestId}</span>
      </div>
      <div className={styles.detailItem}>
        <span>Service ID:</span>
        <span>{request.serviceId}</span>
      </div>
      <div className={styles.detailItem}>
        <span>Description:</span>
        <span>{request.description}</span>
      </div>
      <div className={styles.detailItem}>
        <span>Status:</span>
        <span className={styles[request.status.toLowerCase()]}>{request.status}</span>
      </div>
      <div className={styles.detailItem}>
        <span>Created Date:</span>
        <span>{new Date(request.createDate).toLocaleString()}</span>
      </div>
      <div className={styles.detailItem}>
        <span>Expected Completion:</span>
        <span>{new Date(request.exceptCompletionDate).toLocaleString()}</span>
      </div>
      <button onClick={() => navigate('/viewrequest')} className={styles.backButton}>
        Back to Requests
      </button>
    </div>
  );
};

export default RequestDetail;
