import React, { useState, useEffect } from 'react';
import styles from './ManageRequests.module.css';
import axios from 'axios';

const ManageRequests = () => {
  const [requests, setRequests] = useState([]);
  const [filter, setFilter] = useState('all'); // 'all', 'pending', 'completed'
  const [selectedRequest, setSelectedRequest] = useState(null);
  const [adminComment, setAdminComment] = useState('');

  useEffect(() => {
    fetchRequests();
  }, [filter]);

  const fetchRequests = () => {
    axios.get('http://localhost:8080/requests')
      .then(response => {
        let filteredRequests = response.data;
        if (filter === 'pending') {
          filteredRequests = filteredRequests.filter(req => req.status === 'Pending');
        } else if (filter === 'completed') {
          filteredRequests = filteredRequests.filter(req => req.status === 'Completed');
        }
        setRequests(filteredRequests);
      })
      .catch(error => console.error('Error fetching requests:', error));
  };

  const handleStatusUpdate = (id, newStatus) => {
    axios.put(`http://localhost:8080/requests/${id}/status`, null, {
      params: { status: newStatus }
    })
    .then(response => {
      setRequests(requests.map(req => 
        req.requestId === id ? response.data : req
      ));
      setSelectedRequest(null);
    })
    .catch(error => console.error('Error updating status:', error));
  };

  const handleAddComment = (requestId) => {
    if (!adminComment.trim()) return;
    
    axios.put(`http://localhost:8080/requests/${requestId}/comment`, { comment: adminComment })
      .then(response => {
        setRequests(requests.map(req => 
          req.requestId === requestId ? response.data : req
        ));
        setAdminComment('');
        setSelectedRequest(null);
      })
      .catch(error => console.error('Error adding comment:', error));
  };

  return (
    <div className={styles.container}>
      <h2 className={styles.heading}>Manage Service Requests</h2>
      
      <div className={styles.filterControls}>
        <button 
          className={filter === 'all' ? styles.activeFilter : ''}
          onClick={() => setFilter('all')}
        >
          All Requests
        </button>
        <button 
          className={filter === 'pending' ? styles.activeFilter : ''}
          onClick={() => setFilter('pending')}
        >
          Pending
        </button>
        <button 
          className={filter === 'completed' ? styles.activeFilter : ''}
          onClick={() => setFilter('completed')}
        >
          Completed
        </button>
      </div>

      <div className={styles.requestsGrid}>
        {requests.map((req) => (
          <div key={req.requestId} className={styles.requestCard}>
            <div className={styles.requestHeader}>
              <h3>Request #{req.requestId}</h3>
              <span className={`${styles.status} ${req.status === 'Pending' ? styles.pending : styles.completed}`}>
                {req.status}
              </span>
            </div>
            <p><strong>Service:</strong> {getServiceName(req.serviceId)}</p>
            <p><strong>User ID:</strong> {req.userId}</p>
            <p><strong>Description:</strong> {req.description}</p>
            <p><strong>Submitted:</strong> {new Date(req.createDate).toLocaleString()}</p>
            
            {req.comments && (
              <div className={styles.comments}>
                <strong>Admin Comments:</strong>
                <p>{req.comments}</p>
              </div>
            )}

            <div className={styles.actionButtons}>
              <button 
                onClick={() => handleStatusUpdate(req.requestId, 'Completed')}
                disabled={req.status === 'Completed'}
              >
                Mark Completed
              </button>
              <button onClick={() => setSelectedRequest(req)}>
                Add Comment
              </button>
            </div>
          </div>
        ))}
      </div>

      {selectedRequest && (
        <div className={styles.commentModal}>
          <h3>Add Comment for Request #{selectedRequest.requestId}</h3>
          <textarea
            value={adminComment}
            onChange={(e) => setAdminComment(e.target.value)}
            placeholder="Enter your comments/instructions..."
            className={styles.commentInput}
          />
          <div className={styles.modalActions}>
            <button onClick={() => handleAddComment(selectedRequest.requestId)}>
              Submit Comment
            </button>
            <button onClick={() => setSelectedRequest(null)}>
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

// Helper function to get service name by ID
const getServiceName = (serviceId) => {
  const services = {
    1: 'Plumbing',
    2: 'Electricity',
    3: 'Garbage Collection',
    4: 'Maintenance'
  };
  return services[serviceId] || `Service ${serviceId}`;
};

export default ManageRequests;
