import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './RaiseRequest.module.css';

const RaiseRequest = ({ userId }) => {
  const [serviceId, setServiceId] = useState('');
  const [description, setDescription] = useState('');
  const [message, setMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = () => {
    if (!serviceId || !description) {
      setMessage('Please fill all fields');
      setIsError(true);
      setTimeout(() => setMessage(''), 3000);
      return;
    }

    const newRequest = {
      serviceId: parseInt(serviceId),
      userId: userId,
      description: description,
      status: "Pending",
      createDate: new Date().toISOString()
    };

    fetch('http://localhost:8080/api/requests', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newRequest)
    })
    .then(response => response.json())
    .then(data => {
      setServiceId('');
      setDescription('');
      setIsError(false);
      setMessage('Request submitted successfully!');
      setTimeout(() => setMessage(''), 3000);
    })
    .catch(error => {
      console.error('Error:', error);
      setMessage('Failed to submit request');
      setIsError(true);
      setTimeout(() => setMessage(''), 3000);
    });
  };

  return (
    <div className={styles.requestForm}>
      <h3>Raise Request</h3>
      <select
        value={serviceId}
        onChange={(e) => setServiceId(e.target.value)}
        className={styles.input}
      >
        <option value="">Select Service</option>
        <option value="1">Plumbing</option>
        <option value="2">Electricity</option>
        <option value="3">Garbage Collection</option>
        <option value="4">Maintenance</option>
      </select>

      <textarea
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        className={styles.textarea}
      />

      <button onClick={handleSubmit} className={styles.submitButton}>
        Submit
      </button>

      {message && (
        <p className={isError ? styles.errorMsg : styles.successMsg}>
          {message}
        </p>
      )}

      <button onClick={() => navigate('/userdashboard')} className={styles.backBtn}>
        Back to Dashboard
      </button>
    </div>
  );
}

export default RaiseRequest;
