import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Subject = () => {
  const [semesters, setSemesters] = useState([]);
  const [studentData, setStudentData] = useState(null);

  useEffect(() => {
    // Get data from localStorage
    const form = JSON.parse(localStorage.getItem('studentForm'));
    if (form) {
      setStudentData(form);

      // Create empty semester data
      let tempSemesters = [];
      for (let i = 0; i < Number(form.semCount); i++) {
        tempSemesters.push({
          semesterNumber: i + 1,
          subjects: [{ subjectName: '', grade: '', credits: '' }]
        });
      }
      setSemesters(tempSemesters);
    }
  }, []);

  const handleSubjectChange = (semIndex, subjIndex, field, value) => {
    const updated = [...semesters];
    updated[semIndex].subjects[subjIndex][field] = value;
    setSemesters(updated);
  };

  const addSubject = (semIndex) => {
    const updated = [...semesters];
    updated[semIndex].subjects.push({ subjectName: '', grade: '', credits: '' });
    setSemesters(updated);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Merge studentData + semesters
    const finalData = { ...studentData, semesters: semesters };

    // API call without async/await
    axios.post('http://localhost:8080/api/students/calculate', finalData)
      .then((res) => {
        alert('CGPA: ' + res.data);
      })
      .catch((err) => {
        console.error(err);
      });
  };

  return (
    <div>
      <h2>Enter Subjects</h2>
      <form onSubmit={handleSubmit}>
        {semesters.map((sem, semIndex) => (
          <div key={semIndex} style={{ border: '1px solid gray', margin: '10px', padding: '10px' }}>
            <h3>Semester {sem.semesterNumber}</h3>
            {sem.subjects.map((subj, subjIndex) => (
              <div key={subjIndex} style={{ display: 'flex', gap: '10px' }}>
                <input type="text" placeholder="Subject Name"
                  value={subj.subjectName}
                  onChange={(e) => handleSubjectChange(semIndex, subjIndex, 'subjectName', e.target.value)} />
                <input type="text" placeholder="Grade"
                  value={subj.grade}
                  onChange={(e) => handleSubjectChange(semIndex, subjIndex, 'grade', e.target.value)} />
                <input type="number" placeholder="Credits"
                  value={subj.credits}
                  onChange={(e) => handleSubjectChange(semIndex, subjIndex, 'credits', e.target.value)} />
              </div>
            ))}
            <button type="button" onClick={() => addSubject(semIndex)}>Add Subject</button>
          </div>
        ))}
        <button type="submit">Calculate CGPA</button>
      </form>
    </div>
  );
};

export default Subject;
