import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Subject = () => {
  const navigate = useNavigate();
  const [subjectCounts, setSubjectCounts] = useState([]);

  useEffect(() => {
    const form = JSON.parse(localStorage.getItem("studentForm"));
    if (form) {
      setSubjectCounts(Array(Number(form.semCount)).fill(""));
    }
  }, []);

  const handleSubjectCountChange = (index, value) => {
    const updatedCounts = [...subjectCounts];
    updatedCounts[index] = value;
    setSubjectCounts(updatedCounts);
  };

  const handleNext = () => {
    localStorage.setItem("subjectCounts", JSON.stringify(subjectCounts));
    navigate("/calculate");
  };

  return (
    <div>
      <h2>Enter Number of Subjects per Semester</h2>
      {subjectCounts.map((count, index) => (
        <div key={index}>
          <label>Semester {index + 1} - Number of Subjects:</label>
          <input
            type="number"
            value={count}
            onChange={(e) =>
              handleSubjectCountChange(index, e.target.value)
            }
            min="1"
            required
          />
        </div>
      ))}
      <button onClick={handleNext}>Next</button>
    </div>
  );
};

export default Subject;
