import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './SuperAdminDashboard.module.css';

const SuperAdminDashboard = () => {
  const [stats, setStats] = useState({
    totalRequests: 0,
    completedRequests: 0,
    pendingRequests: 0,
    totalResidents: 0,
    totalAdmins: 0
  });
  const [showUserForm, setShowUserForm] = useState(false);
  const [newUser, setNewUser] = useState({
    name: '',
    email: '',
    password: '',
    role: 'resident'
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('')

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = () => {
    setLoading(true);
    
    axios.all([
      axios.get('http://localhost:8080/requests/data'),
      axios.get('http://localhost:8080/users/data?role=resident'),
      axios.get('http://localhost:8080/users/data?role=admin')
    ])
    .then(axios.spread((requestsRes, residentsRes, adminsRes) => {
      const completed = requestsRes.data.filter(r => r.status === 'Completed').length;
      
      setStats({
        totalRequests: requestsRes.data.length,
        completedRequests: completed,
        pendingRequests: requestsRes.data.length - completed,
        totalResidents: residentsRes.data.length,
        totalAdmins: adminsRes.data.length
      });
      setLoading(false);
    }))
    .catch(error => {
      setError('Failed to load dashboard data');
      console.error(error);
      setLoading(false);
    });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewUser(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleCreateUser = (e) => {
    e.preventDefault();
    setLoading(true);
    
    axios.post('http://localhost:8080/users/save', newUser)
      .then(response => {
        setMessage(`User ${response.data.name} created successfully!`);
        setNewUser({
          name: '',
          email: '',
          password: '',
          role: 'resident'
        });
        fetchDashboardData();
        setTimeout(() => setMessage(''), 3000);
      })
      .catch(error => {
        setError(error.response?.data?.message || 'Failed to create user');
        setTimeout(() => setError(''), 3000);
      })
      .finally(() => {
        setLoading(false);
      });
  };

  if (loading) return <div className={styles.container}>Loading dashboard...</div>;

  return (
    <div className={styles.container}>
      <h2>Super Admin Dashboard</h2>
      
      {error && <div className={styles.error}>{error}</div>}
      {message && <div className={styles.success}>{message}</div>}

      <div className={styles.cards}>
        <div className={styles.card}>
          <h4>Total Requests</h4>
          <p>{stats.totalRequests}</p>
        </div>
        <div className={styles.card}>
          <h4>Completed</h4>
          <p>{stats.completedRequests}</p>
        </div>
        <div className={styles.card}>
          <h4>Pending</h4>
          <p>{stats.pendingRequests}</p>
        </div>
        <div className={styles.card}>
          <h4>Total Residents</h4>
          <p>{stats.totalResidents}</p>
        </div>
        <div className={styles.card}>
          <h4>Total Admins</h4>
          <p>{stats.totalAdmins}</p>
        </div>
      </div>

      <div className={styles.actions}>
        <button 
          onClick={() => setShowUserForm(!showUserForm)} 
          className={styles.createButton}
        >
          {showUserForm ? 'Hide User Form' : 'Create New User'}
        </button>
        <Link to="/manageusers" className={styles.manageButton}>Manage Users</Link>
        <Link to="/manageadmins" className={styles.manageButton}>Manage Admins</Link>
        <Link to="/reports" className={styles.reportsButton}>View Reports</Link>
      </div>

      {showUserForm && (
        <div className={styles.userForm}>
          <h3>Create New User</h3>
          <form onSubmit={handleCreateUser}>
            <div className={styles.formGroup}>
              <label>Name:</label>
              <input
                type="text"
                name="name"
                value={newUser.name}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className={styles.formGroup}>
              <label>Email:</label>
              <input
                type="email"
                name="email"
                value={newUser.email}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className={styles.formGroup}>
              <label>Password:</label>
              <input
                type="password"
                name="password"
                value={newUser.password}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className={styles.formGroup}>
              <label>Role:</label>
              <select
                name="role"
                value={newUser.role}
                onChange={handleInputChange}
                required
              >
                <option value="resident">Resident</option>
                <option value="admin">Admin</option>
              </select>
            </div>
            <button type="submit" disabled={loading}>
              {loading ? 'Creating...' : 'Create User'}
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default SuperAdminDashboard;
